import React, { useState } from 'react';
import './CalendarPicker.css';

const CalendarPicker = () => {
    const [checkInDate, setCheckInDate] = useState('');
    const [checkOutDate, setCheckOutDate] = useState('');

    const handleCheckInChange = (event) => {
        setCheckInDate(event.target.value);
        setCheckOutDate(''); // Reset check-out date if check-in is changed
    };

    const handleCheckOutChange = (event) => {
        if (!checkInDate) {
            alert('Please select a check-in date first.');
            return;
        }
        setCheckOutDate(event.target.value);
    };

    return (
        <div className="calendar-picker">
            <div className="calendar-field">
                <label htmlFor="check-in">Check-In Date:</label>
                <input
                    type="date"
                    id="check-in"
                    value={checkInDate}
                    onChange={handleCheckInChange}
                />
            </div>
            <div className="calendar-field">
                <label htmlFor="check-out">Check-Out Date:</label>
                <input
                    type="date"
                    id="check-out"
                    value={checkOutDate}
                    min={checkInDate} // Ensure the check-out date is >= check-in
                    onChange={handleCheckOutChange}
                />
            </div>
            <div className="selected-dates">
                <p>Check-In: {checkInDate || 'Not selected'}</p>
                <p>Check-Out: {checkOutDate || 'Not selected'}</p>
            </div>
        </div>
    );
};

export default CalendarPicker;
