import React, { useState } from "react";
import "./AirbnbStyleCalendar.css";

const AirbnbStyleCalendar = ({ bookedDates }) => {
    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    const [hoveredDate, setHoveredDate] = useState(null); // For range preview
    const [selectedButton, setSelectedButton] = useState("checkIn"); // Default to Check-In
    const [currentMonth, setCurrentMonth] = useState(new Date());

    const generateDates = (month, year) => {
        const dates = [];
        const totalDays = new Date(year, month + 1, 0).getDate();
        for (let day = 1; day <= totalDays; day++) {
            dates.push(new Date(year, month, day));
        }
        return dates;
    };

    const getTwoMonths = () => {
        const currentYear = currentMonth.getFullYear();
        const currentMonthIndex = currentMonth.getMonth();
        const firstMonth = generateDates(currentMonthIndex, currentYear);
        const secondMonth = generateDates(
            (currentMonthIndex + 1) % 12,
            currentMonthIndex === 11 ? currentYear + 1 : currentYear
        );

        return [
            { month: currentMonthIndex, year: currentYear, dates: firstMonth },
            {
                month: (currentMonthIndex + 1) % 12,
                year: currentMonthIndex === 11 ? currentYear + 1 : currentYear,
                dates: secondMonth,
            },
        ];
    };

    const navigateMonth = (direction) => {
        setCurrentMonth(
            new Date(currentMonth.getFullYear(), currentMonth.getMonth() + direction, 1)
        );
    };

    const isBooked = (date) => {
        return bookedDates.some(
            (bookedDate) => bookedDate.toDateString() === date.toDateString()
        );
    };

    const isDisabled = (date) => {
        if (selectedButton === "checkIn" || !selectedButton) {
            if (checkOutDate && date > checkOutDate) return true;
        } else if (selectedButton === "checkOut" || !selectedButton) {
            if (!checkInDate) return true;
            if (date < checkInDate) return true;
        }
        return isBooked(date);
    };

    const isInRangePreview = (date) => {
        if (checkInDate && hoveredDate && selectedButton === "checkOut") {
            return date > checkInDate && date <= hoveredDate;
        }
        return false;
    };

    const isInRange = (date) => {
        if (checkInDate && checkOutDate) {
            return date > checkInDate && date < checkOutDate;
        }
        return false;
    };

    const isRangeStart = (date) => {
        return checkInDate && date.toDateString() === checkInDate.toDateString();
    };

    const isRangeEnd = (date) => {
        return checkOutDate && date.toDateString() === checkOutDate.toDateString();
    };

    const handleDateClick = (date) => {
        if (selectedButton === "checkIn") {
            setCheckInDate(date);
            setCheckOutDate(null); // Reset check-out date when starting a new selection
            setSelectedButton("checkOut"); // Automatically switch to Check-Out
        } else if (selectedButton === "checkOut") {
            setCheckOutDate(date);
            setSelectedButton("checkIn"); // Automatically switch back to Check-In
        }
    };

    const handleButtonClick = (type) => {
        setSelectedButton(type); // Set the explicitly selected button
    };

    const handleMouseEnter = (date) => {
        if (selectedButton === "checkOut" && checkInDate) {
            setHoveredDate(date); // Update the hovered date for preview
        }
    };

    const handleMouseLeave = () => {
        setHoveredDate(null); // Clear the hovered date when the mouse leaves
    };

    const handleSubmit = () => {
        if (!checkInDate || !checkOutDate) {
            alert("Please select both check-in and check-out dates.");
        } else {
            alert(
                `Check-In: ${checkInDate.toDateString()}, Check-Out: ${checkOutDate.toDateString()}`
            );
        }
    };

    const [firstMonth, secondMonth] = getTwoMonths();

    const renderCalendar = (monthData) => (
        <div className="calendar-month">
            <h3>
                {monthData.month + 1} / {monthData.year}
            </h3>
            <div className="calendar-grid">
                {monthData.dates.map((date) => (
                    <div
                        key={date.toDateString()}
                        className={`calendar-day ${
                            isBooked(date) ? "booked" : ""
                        } ${isDisabled(date) ? "disabled" : ""} ${
                            isRangeStart(date) ? "range-start" : ""
                        } ${isRangeEnd(date) ? "range-end" : ""} ${
                            (checkInDate &&
                                date.toDateString() ===
                                    checkInDate.toDateString()) ||
                            (checkOutDate &&
                                date.toDateString() ===
                                    checkOutDate.toDateString())
                                ? "selected"
                                : ""
                        } ${isInRangePreview(date) ? "range-preview" : ""} ${
                            isInRange(date) ? "range" : ""
                        }`}
                        onClick={() => !isDisabled(date) && handleDateClick(date)}
                        onMouseEnter={() => handleMouseEnter(date)}
                        onMouseLeave={handleMouseLeave}
                    >
                        {date.getDate()}
                    </div>
                ))}
            </div>
        </div>
    );

    return (
        <div className="airbnb-style-calendar">
            <div className="calendar-buttons">
                <button
                    className={selectedButton === "checkIn" ? "active" : ""}
                    onClick={() => handleButtonClick("checkIn")}
                >
                    Select Check-In
                </button>
                <button
                    className={selectedButton === "checkOut" ? "active" : ""}
                    onClick={() => handleButtonClick("checkOut")}
                >
                    Select Check-Out
                </button>
            </div>
            <div className="selected-dates">
                <p>
                    Check-In Date:{" "}
                    {checkInDate ? checkInDate.toDateString() : "Not selected"}
                </p>
                <p>
                    Check-Out Date:{" "}
                    {checkOutDate ? checkOutDate.toDateString() : "Not selected"}
                </p>
            </div>
            <div className="calendar-navigation">
                <button onClick={() => navigateMonth(-1)}>&lt;</button>
                <button onClick={() => navigateMonth(1)}>&gt;</button>
            </div>
            <div className="calendar-container">
                {renderCalendar(firstMonth)}
                {renderCalendar(secondMonth)}
            </div>
            <button className="submit-button" onClick={handleSubmit}>
                Submit
            </button>
        </div>
    );
};

export default AirbnbStyleCalendar;
