import React, { useState } from 'react';
import './CustomDualCalendar.css';

const CustomDualCalendar = () => {
    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    const [currentMonth, setCurrentMonth] = useState(new Date());

    const daysInMonth = (month, year) => new Date(year, month + 1, 0).getDate();

    const generateCalendar = (date) => {
        const days = [];
        const year = date.getFullYear();
        const month = date.getMonth();
        const totalDays = daysInMonth(month, year);

        for (let day = 1; day <= totalDays; day++) {
            const dayDate = new Date(year, month, day);
            days.push(dayDate);
        }

        return days;
    };

    const handleCheckInClick = (date) => {
        setCheckInDate(date);
        setCheckOutDate(null); // Reset check-out when check-in changes
    };

    const handleCheckOutClick = (date) => {
        if (!checkInDate) {
            alert('Please select a check-in date first!');
            return;
        }
        if (date < checkInDate) {
            alert('Check-out date must be on or after the check-in date.');
            return;
        }
        setCheckOutDate(date);
    };

    const renderCalendarDays = (date, onClick, disableBefore) => {
        return generateCalendar(date).map((day, index) => {
            const isDisabled = disableBefore && day < disableBefore;
            const isSelected =
                (checkInDate && day.toDateString() === checkInDate.toDateString()) ||
                (checkOutDate && day.toDateString() === checkOutDate.toDateString());

            return (
                <div
                    key={index}
                    className={`calendar-day ${isSelected ? 'selected' : ''} ${
                        isDisabled ? 'disabled' : ''
                    }`}
                    onClick={() => !isDisabled && onClick(day)}
                >
                    {day.getDate()}
                </div>
            );
        });
    };

    const navigateMonth = (direction) => {
        setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + direction, 1));
    };

    return (
        <div className="custom-dual-calendar">
            <div className="calendar-wrapper">
                <h3>Check-In Calendar</h3>
                <div className="calendar-header">
                    <button onClick={() => navigateMonth(-1)}>&lt;</button>
                    <span>
                        {currentMonth.toLocaleString('default', {
                            month: 'long',
                            year: 'numeric',
                        })}
                    </span>
                    <button onClick={() => navigateMonth(1)}>&gt;</button>
                </div>
                <div className="calendar-grid">
                    {renderCalendarDays(currentMonth, handleCheckInClick, null)}
                </div>
            </div>
            <div className="calendar-wrapper">
                <h3>Check-Out Calendar</h3>
                <div className="calendar-header">
                    <button onClick={() => navigateMonth(-1)}>&lt;</button>
                    <span>
                        {currentMonth.toLocaleString('default', {
                            month: 'long',
                            year: 'numeric',
                        })}
                    </span>
                    <button onClick={() => navigateMonth(1)}>&gt;</button>
                </div>
                <div className="calendar-grid">
                    {renderCalendarDays(currentMonth, handleCheckOutClick, checkInDate)}
                </div>
            </div>
            <div className="selected-dates">
                <p>Check-In: {checkInDate ? checkInDate.toDateString() : 'Not selected'}</p>
                <p>Check-Out: {checkOutDate ? checkOutDate.toDateString() : 'Not selected'}</p>
            </div>
        </div>
    );
};

export default CustomDualCalendar;
