import React, { useState } from 'react';
import './AirbnbStyleCalendar.css';

const AirbnbStyleCalendar = ({ bookedDates }) => {
    const [checkInDate, setCheckInDate] = useState(null);
    const [checkOutDate, setCheckOutDate] = useState(null);
    const [selectedButton, setSelectedButton] = useState("checkIn");

    const today = new Date();

    // Generate dates for a specific month and year
    const generateDates = (month, year) => {
        const dates = [];
        const totalDays = new Date(year, month + 1, 0).getDate();
        for (let day = 1; day <= totalDays; day++) {
            dates.push(new Date(year, month, day));
        }
        return dates;
    };

    // Generate two months for display
    const getTwoMonths = () => {
        const currentMonth = today.getMonth();
        const currentYear = today.getFullYear();

        const firstMonth = generateDates(currentMonth, currentYear);
        const secondMonth = generateDates(currentMonth + 1, currentYear);

        return [firstMonth, secondMonth];
    };

    // Helper to check if a date is booked
    const isBooked = (date) => {
        return bookedDates.some(
            (bookedDate) => bookedDate.toDateString() === date.toDateString()
        );
    };

    // Helper to disable dates based on conditions
    const isDisabled = (date) => {
        if (selectedButton === "checkIn") {
            if (checkOutDate && date > checkOutDate) return true;
        } else if (selectedButton === "checkOut") {
            if (!checkInDate) return true;
            if (date < checkInDate) return true;
        }
        return isBooked(date);
    };

    const handleDateClick = (date) => {
        if (selectedButton === "checkIn") {
            setCheckInDate(date);
            setCheckOutDate(null); // Reset check-out date when check-in is selected
        } else if (selectedButton === "checkOut") {
            if (!checkInDate) {
                alert("Please select a check-in date first.");
                return;
            }
            setCheckOutDate(date);
        }
    };

    const handleSubmit = () => {
        if (!checkInDate || !checkOutDate) {
            alert("Please select both check-in and check-out dates.");
        } else {
            alert(`Check-In: ${checkInDate.toDateString()}, Check-Out: ${checkOutDate.toDateString()}`);
        }
    };

    const [firstMonth, secondMonth] = getTwoMonths();

    const renderCalendar = (dates, title) => (
        <div className="calendar-month">
            <h3>{title}</h3>
            <div className="calendar-grid">
                {dates.map((date) => (
                    <div
                        key={date.toDateString()}
                        className={`calendar-day ${
                            isBooked(date) ? "booked" : ""
                        } ${isDisabled(date) ? "disabled" : ""} ${
                            (checkInDate && date.toDateString() === checkInDate.toDateString()) ||
                            (checkOutDate && date.toDateString() === checkOutDate.toDateString())
                                ? "selected"
                                : ""
                        }`}
                        onClick={() => !isDisabled(date) && handleDateClick(date)}
                    >
                        {date.getDate()}
                    </div>
                ))}
            </div>
        </div>
    );

    return (
        <div className="airbnb-style-calendar">
            <div className="calendar-buttons">
                <button
                    className={selectedButton === "checkIn" ? "active" : ""}
                    onClick={() => setSelectedButton("checkIn")}
                >
                    Select Check-In
                </button>
                <button
                    className={selectedButton === "checkOut" ? "active" : ""}
                    onClick={() => setSelectedButton("checkOut")}
                >
                    Select Check-Out
                </button>
            </div>
            <div className="calendar-container">
                {renderCalendar(firstMonth, "Current Month")}
                {renderCalendar(secondMonth, "Next Month")}
            </div>
            <button className="submit-button" onClick={handleSubmit}>
                Submit
            </button>
        </div>
    );
};

export default AirbnbStyleCalendar;
